import React, { useEffect, useState } from "react";
import { notify } from "../utils";
import { CreateEmployee, UpdateEmployeeById } from "../api";

function AddEmployee({ showModal, setShowModal, fetchEmployees, employeeObj }) {
  const [employee, setEmployee] = useState({
    name: "",
    email: "",
    phone: "",
    department: "",
    salary: "",
    profileImage: null,
    gender: "",
    courses: [], // Array to store selected courses
  });
  const [updateMode, setUpdateMode] = useState(false);

  useEffect(() => {
    if (employeeObj) {
      setEmployee(employeeObj);
      setUpdateMode(true);
    }
  }, [employeeObj]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEmployee({ ...employee, [name]: value });
  };

  const handleFileChange = (e) => {
    setEmployee({ ...employee, profileImage: e.target.files[0] });
  };

  const handleGenderChange = (e) => {
    setEmployee({ ...employee, gender: e.target.value });
  };

  const handleCoursesChange = (e) => {
    const selectedCourses = Array.from(
      e.target.selectedOptions,
      (option) => option.value
    );
    console.log(employee);
    setEmployee({ ...employee, course: selectedCourses });
  };

  const resetEmployeeStates = () => {
    setEmployee({
      name: "",
      email: "",
      phone: "",
      department: "",
      salary: "",
      profileImage: null,
      gender: "",
      courses: [], // Reset courses as well
    });
  };

  const handleAddEmployee = async (e) => {
    e.preventDefault();
    try {
      const { success, message } = updateMode
        ? await UpdateEmployeeById(employee, employee._id)
        : await CreateEmployee(employee);
      if (success) {
        notify(message, "success");
      } else {
        notify(message, "error");
      }
      setShowModal(false);
      resetEmployeeStates();
      fetchEmployees();
      setUpdateMode(false);
    } catch (err) {
      console.error(err);
      notify("Failed to create Employee", "error");
    }
  };

  const handleModalClose = () => {
    
    setShowModal(false);
    setUpdateMode(false);
    resetEmployeeStates();
  };

  return (
    <div
      className={`modal ${showModal ? "d-block" : ""}`}
      tabIndex="-1"
      role="dialog"
      style={{ display: showModal ? "block" : "none" }}
    >
      <div className="modal-dialog" role="document">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">
              {updateMode ? "Update Employee" : "Add Employee"}
            </h5>
            <button
              type="button"
              className="btn-close"
              onClick={() => handleModalClose()}
            ></button>
          </div>
          <div className="modal-body">
            <form onSubmit={handleAddEmployee}>
              <div className="mb-3">
                <label className="form-label">Name</label>
                <input
                  type="text"
                  className="form-control"
                  name="name"
                  value={employee.name}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Email</label>
                <input
                  type="email"
                  className="form-control"
                  name="email"
                  value={employee.email}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Phone</label>
                <input
                  type="text"
                  className="form-control"
                  name="phone"
                  value={employee.phone}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Department</label>
                <select
                  className="form-control"
                  name="department"
                  value={employee.department}
                  onChange={handleChange}
                  required
                >
                  <option value="">Select Department</option>
                  <option value="Engineering">Engineering</option>
                  <option value="Marketing">Marketing</option>
                  <option value="Sales">Sales</option>
                  <option value="HR">HR</option>
                </select>
              </div>
              <div className="mb-3">
                <label className="form-label">Salary</label>
                <input
                  type="text"
                  className="form-control"
                  name="salary"
                  value={employee.salary}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Gender</label>
                <div>
                  <label className="form-check-label me-3">
                    <input
                      type="radio"
                      name="gender"
                      value="Male"
                      checked={employee.gender === "Male"}
                      onChange={handleGenderChange}
                      className="form-check-input"
                    />
                    Male
                  </label>
                  <label className="form-check-label me-3">
                    <input
                      type="radio"
                      name="gender"
                      value="Female"
                      checked={employee.gender === "Female"}
                      onChange={handleGenderChange}
                      className="form-check-input"
                    />
                    Female
                  </label>
                  <label className="form-check-label">
                    <input
                      type="radio"
                      name="gender"
                      value="Other"
                      checked={employee.gender === "Other"}
                      onChange={handleGenderChange}
                      className="form-check-input"
                    />
                    Other
                  </label>
                </div>
              </div>
              <div className="mb-3">
                <label className="form-label">Courses</label>
                <select
                  className="form-control"
                  name="courses"
                  value={employee.courses} // Ensure this is an array
                  onChange={handleCoursesChange}
                  multiple // Allows multiple selection
                  required
                >
                  <option value="Mathematics">Mathematics</option>
                  <option value="Physics">Physics</option>
                  <option value="Chemistry">Chemistry</option>
                  <option value="Biology">Biology</option>
                  <option value="Computer Science">Computer Science</option>
                </select>
              </div>
              <div className="mb-3">
                <label className="form-label">Profile Image</label>
                <input
                  type="file"
                  className="form-control"
                  name="profileImage"
                  onChange={handleFileChange}
                />
              </div>
              <button type="submit" className="btn btn-primary">
                {updateMode ? "Update" : "Save"}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AddEmployee;
