import React, { useEffect, useState } from "react";
import { notify } from "../utils";
import { CreateEmployee, UpdateEmployeeById } from "../api";

function AddEmployee({ showModal, setShowModal, fetchEmployees, employeeObj }) {
  const [employee, setEmployee] = useState({
    name: "",
    email: "",
    phone: "",
    department: "",
    salary: "",
    profileImage: null,
    gender: "",
    courses: [], // Array to store selected courses
  });
  const [updateMode, setUpdateMode] = useState(false);

  useEffect(() => {
    if (employeeObj) {
      setEmployee(employeeObj);
      setUpdateMode(true);
    }
  }, [employeeObj]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEmployee({ ...employee, [name]: value });
  };

  const handleFileChange = (e) => {
    setEmployee({ ...employee, profileImage: e.target.files[0] });
  };

  const handleGenderChange = (e) => {
    setEmployee({ ...employee, gender: e.target.value });
  };

  const handleCoursesChange = (e) => {
    const selectedCourses = Array.from(e.target.selectedOptions, (option) => option.value);
    setEmployee({ ...employee, courses: selectedCourses });
  };

  const resetEmployeeStates = () => {
    setEmployee({
      name: "",
      email: "",
      phone: "",
      department: "",
      salary: "",
      profileImage: null,
      gender: "",
      courses: [],
    });
  };

  const handleAddEmployee = async (e) => {
    e.preventDefault();
    try {
      const { success, message } = updateMode
        ? await UpdateEmployeeById(employee, employee._id)
        : await CreateEmployee(employee);
      if (success) {
        notify(message, "success");
      } else {
        notify(message, "error");
      }
      setShowModal(false);
      resetEmployeeStates();
      fetchEmployees();
      setUpdateMode(false);
    } catch (err) {
      console.error(err);
      notify("Failed to create Employee", "error");
    }
  };

  const handleModalClose = () => {
    setShowModal(false);
    setUpdateMode(false);
    resetEmployeeStates();
  };

  return (
    <div className={`modal ${showModal ? "d-block" : ""}`} tabIndex="-1" role="dialog" style={{ display: showModal ? "block" : "none" }}>
      <div className="modal-dialog" role="document">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">{updateMode ? "Update Employee" : "Add Employee"}</h5>
            <button type="button" className="btn-close" onClick={() => handleModalClose()}></button>
          </div>
          <div className="modal-body">
            <form onSubmit={handleAddEmployee}>
              {/* Existing fields for name, email, etc. */}
              <div className="mb-3">
                <label className="form-label">Courses</label>
                <select
                  className="form-control"
                  name="courses"
                  value={employee.courses} // Ensure this is an array
                  onChange={handleCoursesChange}
                  multiple // Allows multiple selection
                  required
                >
                  <option value="Mathematics">Mathematics</option>
                  <option value="Physics">Physics</option>
                  <option value="Chemistry">Chemistry</option>
                  <option value="Biology">Biology</option>
                  <option value="Computer Science">Computer Science</option>
                </select>
              </div>
              <div className="mb-3">
                <label className="form-label">Profile Image</label>
                <input type="file" className="form-control" name="profileImage" onChange={handleFileChange} />
              </div>
              <button type="submit" className="btn btn-primary">{updateMode ? "Update" : "Save"}</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AddEmployee;
