import React from "react";
import { Link } from "react-router-dom";
import { DeleteEmployeeById } from "../api";
import { notify } from "../utils";

function EmployeeTable({
  employees = [], // Default value to handle undefined employees
  pagination = { currentPage: 1, totalPages: 1 },
  fetchEmployees,
  handleUpdateEmployee,
}) {
  // Add new headers for Gender and Courses
  const headers = [
    "Profile Image",
    "Name",
    "Email",
    "Phone",
    "Department",
    "gender",
    "Courses",
    "Created Date",
    "Actions",
  ];
  const { currentPage, totalPages } = pagination;

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      handlePagination(currentPage + 1);
    }
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      handlePagination(currentPage - 1);
    }
  };

  const handlePagination = (page) => {
    fetchEmployees("", page, 5);
  };

  const handleDeleteEmployee = async (id) => {
    try {
      const { success, message } = await DeleteEmployeeById(id);
      if (success) {
        notify(message, "success");
        fetchEmployees();
      } else {
        notify(message, "error");
      }
    } catch (err) {
      console.error(err);
      notify("Failed to delete Employee", "error");
    }
  };

  // Update TableRow to display new fields for "Gender" and "Courses"
  const TableRow = ({ employee }) => (
    <tr>
      {/* Display profile image */}
      <td>
        {employee.profileImage ? (
          <img
            src={employee.profileImage}
            alt={`${employee.name}'s profile`}
            style={{ width: "50px", height: "50px", borderRadius: "50%" }}
          />
        ) : (
          <span>No Image</span>
        )}
      </td>
      <td>
        <Link to={`/employee/${employee._id}`} className="text-decoration-none">
          {employee.name}
        </Link>
      </td>
      <td>{employee.email}</td>
      <td>{employee.phone}</td>
      <td>{employee.department}</td>
      <td>{employee.gender}</td> {/* Display gender */}
      <td>{employee.courses && employee.courses.join(", ")}</td> {/* Display courses as comma-separated list */}
      <td>{new Date(employee.createdAt).toLocaleDateString()}</td> {/* Format the created date */}
      <td>
        <i
          className="bi bi-pencil-fill text-warning me-4"
          role="button"
          data-bs-toggle="tooltip"
          data-bs-placement="top"
          title="Edit"
          onClick={() => handleUpdateEmployee(employee)}
        ></i>
        <i
          className="bi bi-trash-fill text-danger"
          role="button"
          data-bs-toggle="tooltip"
          data-bs-placement="top"
          title="Delete"
          onClick={() => handleDeleteEmployee(employee._id)}
        ></i>
      </td>
    </tr>
  );

  const pageNumbers = Array.from(
    { length: totalPages },
    (_, index) => index + 1
  );

  return (
    <>
      <table className="table table-striped">
        <thead>
          <tr>
            {headers.map((header, i) => (
              <th key={i}>{header}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {employees && employees.length === 0 ? (
            <tr>
              <td colSpan={headers.length}>Data Not Found</td>
            </tr>
          ) : (
            employees.map((emp) => <TableRow employee={emp} key={emp._id} />)
          )}
        </tbody>
      </table>

      <div className="d-flex justify-content-between align-items-center my-3">
        <span className="badge bg-primary">
          Page {currentPage} of {totalPages}
        </span>
        <div>
          <button
            className="btn btn-outline-primary me-2"
            onClick={handlePreviousPage}
            disabled={currentPage === 1}
          >
            Previous
          </button>
          {pageNumbers.map((page) => (
            <button
              key={page}
              className={`btn btn-outline-primary me-1 ${
                currentPage === page ? "active" : ""
              }`}
              onClick={() => handlePagination(page)}
            >
              {page}
            </button>
          ))}
          <button
            className="btn btn-outline-primary ms-2"
            onClick={handleNextPage}
            disabled={currentPage === totalPages}
          >
            Next
          </button>
        </div>
      </div>
    </>
  );
}

export default EmployeeTable;
